import React from 'react';
import styled from 'styled-components';
import { Container, Col, Row } from 'react-grid-system';
import { faTwitter } from '@fortawesome/free-brands-svg-icons'
import { faLinkedin } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faCoffee } from '@fortawesome/free-solid-svg-icons'




interface Props { }

const Landing: React.FunctionComponent<Props> = () => {
  let d = new Date();
  let currentYear = d.getFullYear();

  return (
    <Main>

      <Container>
        <ContentWrapper>
          <div className="top">
            <div className="row">
              <a href="/"><img src="logo.svg" /></a>
              <Box className="box-top-right-border"></Box>
            </div>
          </div>
          <div className="bottom">
            <div className="row">
              <Box className="box-bottom-left-border"></Box>
              <a id="myworks" href="/portfolio">My Works</a>
              <div className="social">
                <FontAwesomeIcon icon={faTwitter} />
                <FontAwesomeIcon icon={faLinkedin} />
                / {currentYear}

                </div>
            </div>
          </div>
        </ContentWrapper>
      </Container>
      <div className="mid">
        <div className="main-text">
          <div>Puskal</div>
          <div>Design</div>
        </div>
      </div>
    </Main>
  );
};
const Main = styled.main`
.mid{
  justify-content: center;
  font-size:150px;
  font-weight:bold;
  text-transform: uppercase;
  position: fixed;
  left: 50%;
  top: 50%;
  transform: translate(-50%,-50%);
  .main-text>div:last-child{
    position: relative;
    transform: translateY(-61%);
    background: ${({ theme }) => theme.colors.primary.light};
    line-height: 100px;
    letter-spacing: -1.7px;
  }
  &:after{
    content:'';
    position:fixed;
    left:50%;
    bottom:-2vh;
    width:4px;
    height:43px;
    background:${({ theme }) => theme.colors.primary.dark};
  }
}
`;
const ContentWrapper = styled.div`
display:flex;
flex-direction:column;
justify-content: space-aroung;
&>div{
  height:50vh;
  max-height:50vh;
  min-height:50vh;
  display: flex;
  align-items: flex-end;
  .row{
    flex:1;
    display:flex;
    justify-content:space-between;
  }
  &.top{
    align-items: flex-start;
    .row{
      padding-top:${({ theme }) => theme.spacing.xxxl};

    }
  }
  &.bottom{
    #myworks{
      transform: translateX(-50%);
      text-transform: uppercase;
      font-weight:lighter;
      color:${({ theme }) => theme.colors.primary.dark};
    }
    .row{
      padding-bottom:${({ theme }) => theme.spacing.xxxl};
      align-items: flex-end;
    }

  }

}
`;
const Box = styled.div`
    width:25vh;
    height:25vh;;
    border:1px solid rgba(4,17,26,.2);
    &.box-top-right-border{
      border-bottom:none;
      border-left:none;
    }
    &.box-bottom-left-border{
      border-top:none;
      border-right:none;
    }
`;


export default Landing;
