import React from 'react';
import styled from 'styled-components';
import { Container, Col, Row } from 'react-grid-system';
import SwiperCore, { Navigation } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import TitleBlock from './TitleBlock';
import SingleItemBlock from './SingleItemBlock';
// install Swiper components
SwiperCore.use([Navigation]);

interface Props {
  title1: string;
  title2: string;
  list: any;
}

const SliderBlocks: React.FC<Props> = (props) => {
  const { title1, title2, list } = props;

  const listSlide = () => {
    return list?.map((item: any) => {
      return (
        <SwiperSlide key={item?.id}>
          <SingleItemBlock item={item} />
        </SwiperSlide>
      );
    });
  };

  return (
    <SliderBlock>
      <Container>
        <Row>
          <Col md={10}>
            <TitleBlock>
              <h2 className={'title'}>
                {title1} <span>{title2}</span>
              </h2>
              <p className={'titleText'}>
                A good hotel can make or break a trip. We have crafted a list of
                the most booked hotels to help make your choices easier. Stay in
                this luxurious beautiful hotels and have a relaxing holiday.
              </p>
            </TitleBlock>
          </Col>
        </Row>

        <Row>
          <Col>
            <div className="swiper-button-holder">
              <span className="swiper-button swiper-button-prev" />
              <span className="swiper-button swiper-button-next" />
            </div>
            <Swiper
              spaceBetween={30}
              slidesPerView={3}
              navigation={{
                nextEl: '.swiper-button-next',
                prevEl: '.swiper-button-prev',
              }}>
              {listSlide()}
            </Swiper>
          </Col>
        </Row>
      </Container>
    </SliderBlock>
  );
};
const SliderBlock = styled.section`
  .swiper-button-holder {
    display: flex;
    justify-content: flex-end;
    margin-bottom: ${({ theme }) => theme.spacing.m};

    .swiper-button {
      border: 1px solid ${({ theme }) => theme.colors.mute};
      width: 30px;
      height: 30px;
      position: inherit;
      margin-top: inherit;

      &:focus {
        outline: none;
      }
      &.swiper-button-prev {
        margin-right: ${({ theme }) => theme.spacing.s};
      }
      &::after {
        font-size: ${({ theme }) => theme.fontSize.m};
      }
    }
  }

  .swiper-container {
    @media (min-width: 768px) {
      padding: 20px 15px;
      margin: 20px -15px;
    }
    .swiper-slide-next {
      > div {
        box-shadow: 0px 5px 24px #9999994d;
      }
    }
  }
`;

export default SliderBlocks;
