import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';

// import { useContext } from 'react';
// import { Context } from './../../context/auth';

interface Props {
  item: any;
}

const SingleItemBlock: React.FC<Props> = (props) => {
  const { item } = props;

  // const { agent: authAgent } = useContext(Context);

  // console.log(!authAgent.agent.name);
  // console.log("AUTHAGENT IS", authAgent);
  // const authAgent = false;

  return (
    // <Link href={"/hotels-detail?hotelId=" + item.id}>
    <Link href={`/hotels-detail?hotelId=${item.id}&destination=${item.name}`}>
      <SingleItem>
        <div className="image-holder">
          <img src={'assets/img/slider-1.png'} alt={item?.name} />
        </div>
        <SliderCaption>
          <h4 className="title">{item?.name}</h4>
          <p className="nearby">
            ({item?.city} {item?.country})
          </p>
          <p>{item?.excerpt}</p>
          <div className="price">
              <del>€ {item?.base_price}</del>€ {item?.cut_off_price}
            </div>

{/*           
          {!authAgent?.agent?.name && (
            <div className="price">
              <del>€ {item?.base_price}</del>€ {item?.cut_off_price}
            </div>
          )} */}
        </SliderCaption>
      </SingleItem>
    </Link>
  );
};
const SingleItem = styled.div`
   box-shadow:0px 3px 15px #99999929;
   cursor: pointer;
   .image-holder{
      height: 250px;   
      img{
        height: 100%;
        width: 100%;
        object-fit: cover:
      }
   }

`;

const SliderCaption = styled.div`
  padding: ${({ theme }) => theme.spacing.m};
  background: ${({ theme }) => theme.colors.white};
  .title {
    margin: 0;
  }
  p {
    font-size: ${({ theme }) => theme.fontSize.s};
    line-height: ${({ theme }) => theme.spacing.l};
    &.nearby {
      color: ${({ theme }) => theme.colors.primary.default};
    }
  }
  .price {
    display: flex;
    align-items: center;
    font-size: ${({ theme }) => theme.fontSize.xxl};
    font-weight: 500;
    font-family: ${({ theme }) => theme.font.number};
    color: ${({ theme }) => theme.colors.heading};
    del {
      font-size: ${({ theme }) => theme.fontSize.m};
      margin-right: ${({ theme }) => theme.spacing.m};
      color: ${({ theme }) => theme.colors.mute};
      &:after,
      &:before {
        content: '';
        width: 15px;
      }
      &:after {
      }
    }
  }
`;

export default SingleItemBlock;
