import React from 'react';
import styled, { css } from 'styled-components';
import Nav from './Nav';
import 'react-day-picker/lib/style.css';
import { Container, Col, Row, setConfiguration } from 'react-grid-system';
import ItemSearch from './ItemSearch';

setConfiguration({ maxScreenClass: 'xl', gutterWidth: 30 });

interface Props {
  size?: string;
  backgroundImage?: string;
  className?: string;
  itemSearch?: boolean;
}

const Header: React.FC<Props> = (props) => {
  const { size, itemSearch } = props;
  return (
    <>
      <StyledHeader
        style={{ backgroundImage: `url(${props.backgroundImage})` }}
        className={`header ${props?.className ? props?.className : ''}`}>
        <Nav />
        <BannerCaption size={size}>
          <Container>
            <Row>
              <Col sm={12} md={12}>
                <div className="bannercontent">{props.children}</div>
              </Col>
            </Row>
          </Container>
        </BannerCaption>
      </StyledHeader>
    </>
  );
};

const StyledHeader = styled.header`
  display: flex;
  flex-direction: column;

  background-size: cover;
  position: relative;
  z-index: 2;

  &:before {
    content: '';
    background-color: rgba(27, 33, 39, 0.5);
    position: absolute;
    left: 0;
    bottom: 0;
    top: 0;
    right: 0;
    z-index: -1;
  }
`;

const BannerCaption = styled.div`
  flex-grow: 1;
  color: ${({ theme }) => theme.colors.white};

  .bannercontent {
    display: flex;
    align-items: center;
    justify-content: space-between;

    ${(props: Props) =>
      (props.size === 'large' &&
        css`
          height: 70vh;
        `) ||
      (props.size === 'medium' &&
        css`
          height: 45vh;
        `) ||
      (props.size === 'small' &&
        css`
          height: 30vh;
        `)}

    @media (min-width: 768px) {
      margin-bottom: ${({ theme }) => theme.spacing.xxxxl};
    }
    margin-bottom: ${({ theme }) => theme.spacing.xxl};
    .badge {
      width: ${({ theme }) => theme.spacing.xl};
      height: ${({ theme }) => theme.spacing.xl};
      border-radius: 50px;

      display: flex;
      align-items: center;
      justify-content: center;

      background: ${({ theme }) => theme.colors.white};

      i {
        color: ${({ theme }) => theme.colors.text};
      }
    }
    .inner {
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      @media screen and (min-width: 768px) {
        width: 80%;
      }
      .heading {
        color: ${({ theme }) => theme.colors.white};
        @media screen and (max-width: 768px) {
          font-size: ${({ theme }) => theme.fontSize.xl};
        }
      }
      p {
        @media screen and (max-width: 768px) {
          font-size: ${({ theme }) => theme.fontSize.sm};
        }
      }
      .divider {
        width: 50%;
        height: 1px;
        background: rgba(255, 255, 255, 0.3);
        margin-top: ${({ theme }) => theme.spacing.sm};
        margin-bottom: ${({ theme }) => theme.spacing.xl};
      }
      .exploremore {
        color: ${({ theme }) => theme.colors.white};
      }
    }
  }
`;

export default Header;
