import React, { useState } from 'react';
import styled from 'styled-components';
import { Container, Col, Row, setConfiguration } from 'react-grid-system';
import { faTwitter } from '@fortawesome/free-brands-svg-icons';
import { faLinkedin } from '@fortawesome/free-brands-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { SRLWrapper } from "simple-react-lightbox";
import Data from "../../data/data.json";


setConfiguration({ containerWidths: [540, 740, 960, 1140, 1140] });

interface Props { }



const Landing: React.FunctionComponent<Props> = () => {
  const mainData = Data.data;
  const [data, setFilterData] = useState(mainData);

  let d = new Date();
  let currentYear = d.getFullYear();
  let el: any[];
  function filterCard(e, slug) {
    e.preventDefault();
    if(slug){
      let filteredData: any = Object.values(mainData).filter(function (d) {
        return d.slug === slug;
      });
      setFilterData(filteredData);
    }else{
      setFilterData(Data.data);
    }

  }

  return (
    <Main>
      <ContentWrapper>
        <div className="top">
          <div className="row">
            <a href="/"><img src="logo.svg" /></a>
            <Box className="box-top-right-border"></Box>
          </div>
        </div>
        <div className="mid">
          <div className="main-text">
            <div>PORTFOLIO</div>
            <div id="filters">
              <a href="#"
                onClick={(e) => {
                  filterCard(e, '');
                }}
              >All</a>/
            <a href="#" onClick={(e) => {
                filterCard(e, 'design');
              }}>Design</a>/
            <a href="#"
                onClick={(e) => {
                  filterCard(e, 'development');
                }}
              >Development</a>/
            <a href="#"
            onClick={(e)=>{
              filterCard(e,'art');
            }}
            >Art</a>/
            <a href="#"
            onClick={(e)=>{
              filterCard(e,'photography');
            }}
            >Photography</a>
            </div>

          </div>
        </div>
        <Container className="container">
          <WorkList>
            <SRLWrapper>
              {Object.values(data).map((item, i) => (
                <a key={i} className="item">
                  <div className="inner" style={{ backgroundImage: `url(/assets/img/work/${item.img})` }}>
                    <img src={`/assets/img/work/${item.img}`} alt={`${item.desc}`} />
                  </div>
                </a>
              ))}
            </SRLWrapper>
          </WorkList>
        </Container>
        <div className="bottom">
          <div className="row">
            <Box className="box-bottom-left-border"></Box>
            <div className="social">
              <a href="https://twitter.com/Puskal" target="_blank"><FontAwesomeIcon icon={faTwitter} /></a>
              <a href="https://www.linkedin.com/in/puskal/" target="_blank"><FontAwesomeIcon icon={faLinkedin} /></a>
                / <span>{currentYear}</span>
            </div>
          </div>
        </div>
      </ContentWrapper>

    </Main>
  );
};
const WorkList = styled.div`
padding-bottom:${({ theme }) => theme.spacing.xxxxxl};
width:100%;
&>div{
  display:flex;
  flex-wrap: wrap;
  width:100%;
}

a.item{
  width: 33.33%;
  height: 300px;
  padding:5px;
  box-sizing: border-box;
  &:last-child{
    margin:auto;
  }
  .inner{
    background: #fff;
    height:100%;
    width:100%;
    background-size:cover;
    overflow:hidden;
    cursor:pointer;
    border:1px solid #e8e7e3;
    box-shadow: 0 4px 4px -3px #8c8c8c;
    transition: box-shadow 0.3s ease-in-out;
    &:hover{
      box-shadow: 0 8px 8px -3px rgba(140,140,140,.7);
    }
    img{
      opacity:0;
    }
  }
}


`;

const Main = styled.main`
.mid{
  justify-content: center;
  font-size:150px;
  font-weight:bold;
  text-transform: uppercase;
  .main-text{
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-bottom:${({ theme }) => theme.spacing.xxxxxl};
  &>div{
    position: relative;
    // transform: translateY(-61%);
    background: ${({ theme }) => theme.colors.primary.light};
    letter-spacing: -8px;
  }
}

  #filters{
    // transform: translateX(-50%);
    text-transform: uppercase;
    color:${({ theme }) => theme.colors.primary.dark};
    // position: absolute;
    // bottom: 65px;
    // left: 50%;
    background: none;
    font-weight:300;
    display: flex;
    font-size:${({ theme }) => theme.fontSize.l};
    a{
      font-weight:300;
      display:inline-block;
      padding:0 ${({ theme }) => theme.spacing.s} 0 ${({ theme }) => theme.spacing.sm};
      font-size:${({ theme }) => theme.fontSize.l};
      letter-spacing: normal;
      line-height: initial;
      background: none;
      color:${({ theme }) => theme.colors.primary.dark};
      &:hover{
        color:${({ theme }) => theme.colors.primary.default};
      }
    }
  }
}
`;
const ContentWrapper = styled.div`
display:flex;
flex-direction:column;
justify-content: space-aroung;
padding:0 ${({ theme }) => theme.spacing.xxxxl};
.container{
  width:100%;
}
&>div{
  display: flex;
  align-items: flex-end;
  .row{
    flex:1;
    display:flex;
    justify-content:space-between;
  }
  &.top{
    align-items: flex-start;
    .row{
      padding-top:${({ theme }) => theme.spacing.xxxl};

    }
  }
  &.bottom{
    .social{
      display: flex;
      align-items: center;
      a{
        color:${({ theme }) => theme.colors.primary.default};
      }
      color:${({ theme }) => theme.colors.primary.default};
      svg{
        margin-right:${({ theme }) => theme.spacing.sm};
        width:18px;
      }
      span{
        margin-left:${({ theme }) => theme.spacing.sm};
      }
    }
    .row{
      padding-bottom:${({ theme }) => theme.spacing.xxxl};
      align-items: flex-end;
      position:relative;
    }

  }

}
`;
const Box = styled.div`
    width:25vh;
    height:25vh;;
    border:1px solid rgba(4,17,26,.2);
    &.box-top-right-border{
      border-bottom:none;
      border-left:none;
    }
    &.box-bottom-left-border{
      border-top:none;
      border-right:none;
    }
`;


export default Landing;
