import React, { useEffect, useState } from 'react';

import styled from 'styled-components';
import { Container, Row, Col } from 'react-grid-system';
import DayPickerInput from 'react-day-picker/DayPickerInput';
import Select, { components } from 'react-select';
import Button from './common/Button';
import { useRouter } from 'next/router';

import { getAutoSuggestions } from 'utils/api';

import LocationMarkerIcon from '../public/assets/icon/location-marker.svg';
import HotelIcon from '../public/assets/icon/hotel.svg';
import Icon from './common/Icon';

interface Props {}

const options = [
  { value: '1', label: '1' },
  { value: '2', label: '2' },
  { value: '3', label: '3' },
];

const { Option } = components;
const IconOption = (props: any) => (
  <Option {...props}>
    {props?.data?.type === 'hotel' ? (
      <Icon icon={<HotelIcon />} />
    ) : (
      <Icon icon={<LocationMarkerIcon />} />
    )}
    {props?.data?.label}
  </Option>
);

const ItemSearch: React.FC<Props> = () => {
  const { query } = useRouter();
  const router = useRouter();

  const [suggestDest, setSuggestDest] = useState([]);
  const [activeTab, setActiveTab] = useState('HOTELS');

  const switchToTours = () => setActiveTab('TOURS');

  const switchToHotels = () => setActiveTab('HOTELS');

  const defaultCheckIn = new Date(
    new Date().setDate(new Date().getDate() + 14)
  ).toLocaleDateString();
  const defaultCheckOut = new Date(
    new Date().setDate(new Date().getDate() + 21)
  ).toLocaleDateString();

  const [searchParams, setSearchParams] = useState<any>({});

  useEffect(() => {
    setSearchParams({
      destination: query?.destination,
      checkIn: query?.checkIn || defaultCheckIn,
      checkOut: query?.checkOut || defaultCheckOut,
      people: query?.people,
      selectedOption: query?.destination
        ? { label: query?.destination, value: query?.destination }
        : null,
    });
  }, [query, defaultCheckIn, defaultCheckOut]);

  const datePickerCheckIn = new Date(searchParams?.checkIn?.toString());
  const datePickerCheckOut = new Date(searchParams?.checkOut?.toString());
  const disabledCheckIn = new Date();
  const disabledCheckOut = new Date(
    new Date().setDate(
      new Date(searchParams?.checkIn?.toString()).getDate() + 1
    )
  );

  const handleDestinationSelectionChange = (selectedOption: any) => {
    setSearchParams((prevState: any) => {
      return {
        ...prevState,
        destination: selectedOption.value,
        selectedOption,
      };
    });
  };

  const handleDestinationInputChange = (value: string) => {
    fetchSuggestions(value);
  };

  const fetchSuggestions = async (value: string) => {
    const autoSuggestions = await getAutoSuggestions(value);

    let newMappedArray = [];

    if (!(autoSuggestions?.data === null)) {
      newMappedArray = autoSuggestions?.data?.map?.((suggestion: any) => {
        return {
          ...suggestion,
          value: suggestion.name,
          label: suggestion.name,
        };
      });
    }

    setSuggestDest(newMappedArray);
  };

  const handleSubmit = (e: any) => {
    if (searchParams?.selectedOption?.type === 'hotel') {
      router.push({
        pathname: '/hotels-detail',
        query: {
          hotelId: searchParams?.selectedOption?.id,
          destination: searchParams?.destination,
          checkIn: searchParams?.checkIn,
          checkOut: searchParams?.checkOut,
          people: searchParams?.people,
        },
      });
    } else {
      router.push({
        pathname: '/search',
        query: {
          destination: searchParams?.destination,
          checkIn: searchParams?.checkIn,
          checkOut: searchParams?.checkOut,
          people: searchParams?.people,
          searchType: searchParams?.selectedOption?.type,
        },
      });
    }
    e.preventDefault();
  };

  const handleCheckIn = (event: Date) => {
    setSearchParams((prevState: any) => {
      return {
        ...prevState,
        checkIn: event.toLocaleDateString(),
      };
    });
  };

  const handleCheckOut = (event: Date) => {
    setSearchParams((prevState: any) => {
      return {
        ...prevState,
        checkOut: event.toLocaleDateString(),
      };
    });
  };

  return (
    <SearchContainer>
      <StyledContainer>
        <Row>
          <Col>
            <div className="filters">
              <div className="tabs">
                <div className={activeTab === 'HOTELS' ? 'tab active' : 'tab'}>
                  <span onClick={switchToHotels}>
                    <i className="fas fa-hotel"></i>HOTELS
                  </span>
                </div>
                <div className={activeTab === 'TOURS' ? 'tab active' : 'tab'}>
                  <span onClick={switchToTours}>
                    <i className="fa fa-briefcase" aria-hidden="true"></i>TOURS
                  </span>
                </div>
              </div>
              <form onSubmit={handleSubmit} action="/hotels-search">
                <div className="filter-wrap">
                  <Container fluid>
                    <Row className="filter-items">
                      <Col xs={12} md={6} lg={3} className="form-group">
                        <i className="fas fa-search"></i>

                        <Select
                          value={searchParams?.selectedOption}
                          onChange={handleDestinationSelectionChange}
                          onInputChange={handleDestinationInputChange}
                          options={suggestDest}
                          openMenuOnClick={false}
                          placeholder="Select Destination"
                          components={{ Option: IconOption }}
                          className="custom-select"
                          classNamePrefix="custom-select"
                        />
                      </Col>
                      <Col xs={6} md={6} lg={3} className="form-group">
                        <i className="fas fa-calendar"></i>
                        <DayPickerInput
                          placeholder={'Check-In'}
                          onDayChange={handleCheckIn}
                          value={datePickerCheckIn}
                          dayPickerProps={{
                            modifiers: {
                              disabled: [{ before: disabledCheckIn }],
                            },
                          }}
                        />
                      </Col>
                      <Col xs={6} md={6} lg={3} className="form-group">
                        <i className="fas fa-calendar"></i>
                        <DayPickerInput
                          placeholder={'Check-Out'}
                          onDayChange={handleCheckOut}
                          value={datePickerCheckOut}
                          dayPickerProps={{
                            modifiers: {
                              disabled: [
                                {
                                  before: disabledCheckOut,
                                },
                              ],
                            },
                          }}
                        />
