import styled from 'styled-components';

interface StyledProps {
  flex?: string | number;
  width?: string;
  justify?: string;
  align?: string;
  wrap?: string;
}

const FlexVertical = styled.div`
  display: flex;
  flex: ${(props: StyledProps) => props.flex};
  width: ${(props: StyledProps) => props.width};
  justify-content: ${(props: StyledProps) => props.justify || 'inherit'};
  align-items: ${(props: StyledProps) => props.align || 'inherit'};
  flex-wrap: ${(props: StyledProps) => props.wrap || 'inherit'};
  flex-direction: column;
`;

export default FlexVertical;
