import styled from 'styled-components';
import React from 'react';
import { Container } from 'react-grid-system';

const links = [
  {
    href:
      '/search?destination=&checkIn=12%2F25%2F2020&checkOut=1%2F1%2F2021&people=&searchType=',
    label: 'Hotels',
  },
  { href: '/tours', label: 'Tours' },
  { href: '/agency', label: 'Agency' },
  { href: '/stories', label: 'Stories' },
];

interface Props {}

const Nav: React.FC<Props> = () => {
  function handleClick(e) {
    e.preventDefault();
    console.log(e);
  }
  return (
    <MainNav>
      <Container>
        <NavbarTop>
          <ul>
            <li className="contact-menu">
              <a href="tel:+4946518898570">
                <span>
                  <i
                    className="fa fa-phone fa-flip-horizontal"
                    aria-hidden="true"></i>
                  <a href="tel:+4946518898570">+49 4651 889 8570</a>
                </span>
              </a>
            </li>
            <li className="social-link">
              <a href="#">
                <span>
                  <i className="fab fa-facebook-f"></i>
                </span>
              </a>
            </li>
            <li className="social-link">
              <a href="#">
                <span>
                  <i className="fab fa-twitter"></i>
                </span>
              </a>
            </li>
            <li className="social-link">
              <a href="#">
                <span>
                  <i className="fab fa-instagram"></i>
                </span>
              </a>
            </li>

            <li>
              <LanguageSwitcher className="dropdown">
                <button>EN<i className="fas fa-chevron-down"></i></button>
                <div className="dropdown-content">
                  <a href="#" onClick={handleClick}>
                    <span>English</span>
                  </a>
                  <a href="#" onClick={handleClick}>
                    <span>Deutsch</span>
                  </a>
                </div>
              </LanguageSwitcher>
            </li>
          </ul>
        </NavbarTop>
        <Navbar>
          <SiteLogo>
            <div className={'headerMenu'}>
              <a href="./">
                <i className="fa fa-bars" aria-hidden="true"></i>
              </a>
            </div>
            <a href="./">
              <img
                src={'assets/img/logo.png'}
                alt="Flamingo Logo"
                className={'brandIcon'}
              />
            </a>
            <div className={'headerIcon'}>
              <a href="./">
                <i className="fa fa-phone" aria-hidden="true"></i>
              </a>
              <a href="./">
                <i className="fa fa-heart" aria-hidden="true"></i>
              </a>
            </div>
          </SiteLogo>
          <Navigation>
            {links.map(({ href, label }, index: number) => (
              <li key={index}>
                <a href={href}>
                  <span>{label}</span>
                </a>
              </li>
            ))}

          </Navigation>
        </Navbar>
      </Container>
    </MainNav>
  );
};

const SiteLogo = styled.div`
  @media (max-width: 767px) {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
  }
  .brandIcon {
    @media (max-width: 576px) {
      height: ${({ theme }) => theme.spacing.xl};
    }
  }
  a {
    color: ${({ theme }) => theme.colors.white};
  }
  .headerIcon {
    a {
      padding: ${({ theme }) => theme.spacing.xs};
    }
  }
  @media (min-width: 767px) {
    .headerMenu,
    .headerIcon {
      display: none;
    }
  }
`;

const Navigation = styled.ul`
  display: flex;
  margin: 0;
  padding: 0;
  list-style: none;

  li {
    padding: 0 ${({ theme }) => theme.spacing.m};
    a {
      color: ${({ theme }) => theme.colors.white};
      font-size: ${({ theme }) => theme.fontSize.sm};
    }
    &.active a {
      position: relative;
      &:after {
        content: '';
        position: absolute;
        bottom: -34.5px;
        left: 0;
        height: 4px;
        width: 100%;
        background: ${({ theme }) => theme.colors.white};
      }
    }
  }
  @media screen and (max-width: 767px) {
    display: none;
  }
`;

const Navbar = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;
const MainNav = styled.nav`
  border-bottom: 1px solid rgba(255, 255, 255, 0.3);
  padding: ${({ theme }) => theme.spacing.m} 0;
`;
const NavbarTop = styled.div`
display:flex;
@media (max-width: 767px) {
  display:none;
}
justify-content:flex-end;
  ul{
    margin: 0 0 ${({ theme }) => theme.spacing.xs} 0;
    display:flex;
    align-items:center;
    list-style-type:none;
    li{
      padding: 0 ${({ theme }) => theme.spacing.m};
      &.social-link{
        padding: 0 ${({ theme }) => theme.spacing.s};
      }
      a,i{
      color: ${({ theme }) => theme.colors.white};
      }
    &.contact-menu{
      i{
        margin-right: ${({ theme }) => theme.spacing.s};
      }
    }
    }
  }

`;

const LanguageSwitcher = styled.div`
  position: relative;
  display: inline-block;

  button{
    background:transparent;
    border:none;
    color:#fff;
    cursor:pointer;
    i{
      color:#fff;
      margin-left:${({ theme }) => theme.spacing.xs};
    }
  }

.dropdown-content {
  display: none;
  position: absolute;
  background-color: ${({ theme }) => theme.colors.white};
  min-width: 84px;
  left: 50%;
  transform: translateX(-50%);
  top: 21px;
  z-index: 10;
  border: 1px solid ${({ theme }) => theme.colors.primary.light};
}

// Links inside the dropdown 
.dropdown-content a {
  color: black;
  padding: ${({ theme }) => theme.spacing.s};
  text-decoration: none;
  display: block;
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  &:last-child{
    border-bottom: none;
  }
}

.dropdown-content a:hover {background-color: ${({ theme }) => theme.colors.border};}

&:hover .dropdown-content {
  display: block;
}
`;


export default Nav;
