import React from 'react';
import FlexVertical from 'components/common/FlexVertical';
import Header from 'components/Header';
import Footer from 'components/Footer';
import styled from 'styled-components';
import { Container, Col, Row } from 'react-grid-system';

import SwiperCore, { Navigation } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import SliderBlocks from '../../components/common/SliderBlocks';

import { useQuery } from 'react-query';
import { fetchFeaturedHotels } from 'utils/api';
import ItemSearch from '../../components/ItemSearch';

// install Swiper components
SwiperCore.use([Navigation]);

interface Props {}

const Landing: React.FunctionComponent<Props> = () => {
  const { status, data: featuredHotelsData } = useQuery(
    'featuredHotels',
    fetchFeaturedHotels
  );

  return (
    <FlexVertical>
      <Header backgroundImage={'/assets/img/banner1.jpg'} size={'large'}>
        <div className={'inner'}>
          <h2 className={'heading'}>EXPLORE THE WORLD</h2>
          <p>
            Explore the world together with Flamingo Tours. We guarantee that
            your next holiday will have all theelements that it deserves. With
            Flamingo, you only need to worry about how much fun you will
            haveandleave all the rest to us.
          </p>
          <div className={'divider'} />
          <a className={'exploremore'} href="">
            Explore <i className="fas fa-long-arrow-alt-right"></i>
          </a>
        </div>
      </Header>
      <ItemSearch />

      <Section>
        <Container>
          <Row align={'center'}>
            <Col lg={6}>
              <Swiper
                spaceBetween={30}
                slidesPerView={2}
                navigation={{
                  nextEl: '.swiper-button-next',
                  prevEl: '.swiper-button-prev',
                }}>
                <SwiperSlide>
                  <SlideHolder>
                    <img src={'assets/img/girl.png'} alt="" />
                    <div className="slidetitle">Good Reception</div>
                  </SlideHolder>
                </SwiperSlide>
                <SwiperSlide>
                  <SlideHolder>
                    <img src={'assets/img/bycycle.png'} alt="" />
                    <div className="slidetitle">Warm Welcome</div>
                  </SlideHolder>
                </SwiperSlide>
                <SwiperSlide>
                  <SlideHolder>
                    <img src={'assets/img/dog.png'} alt="" />
                    <div className="slidetitle">Free Drinks</div>
                  </SlideHolder>
                </SwiperSlide>
              </Swiper>
              <SwiperControl className="swiper-button-holder">
                <span className="swiper-button swiper-button-prev" />
                <span className="swiper-button swiper-button-next" />
              </SwiperControl>
            </Col>
            <Col lg={5}>
              <div className={'landing-text'}>
                <h2>Why choose us</h2>
                <p className={'small'}>
                  We at Flamingo are passionate about providing the best travel
                  experiences for you. Our passion touches each and every area
                  of travelling. Flamingo believes that every travel is an
                  unique experience and we make sure that you make the most out
                  of it. We love answering all your questions so start planning
                  your next vacation now!
                </p>
              </div>
            </Col>
          </Row>
        </Container>
      </Section>

      {status && (
        <SliderBlocks title1="Best" title2="Hotels" list={featuredHotelsData} />
      )}

      <SliderBlocks title1="Best" title2="Tours" list={featuredHotelsData} />

      <Footer />
    </FlexVertical>
  );
};

const SwiperControl = styled.div`
  display: flex;
  justify-content: flex-start;
  margin-top: ${({ theme }) => theme.spacing.xs};

  .swiper-button {
    position: inherit;
    margin-top: inherit;
    width: ${({ theme }) => theme.spacing.xxl};

    &:after {
      font-family: 'Font Awesome 5 Pro';
      color: ${({ theme }) => theme.colors.primary.default};
      font-size: ${({ theme }) => theme.fontSize.xxxl};
    }

    &:focus {
      outline: none;
    }
    &.swiper-button-disabled {
      &:after {
        color: ${({ theme }) => theme.colors.heading};
        font-size: ${({ theme }) => theme.fontSize.l};
      }
    }
    &.swiper-button-next {
      &:after {
        content: '\f30b';
      }
    }
    &.swiper-button-prev {
      &:after {
        content: '\f30a';
      }
    }
  }
`;

const SlideHolder = styled.div`
  height: 160px;
  @media (min-width: 576px) {
    height: 260px;
  }

  overflow: hidden;
  position: relative;
  img {
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
  .slidetitle {
    position: absolute;
    left: 0;
    bottom: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.55);
    text-align: center;
    padding: ${({ theme }) => theme.spacing.m};
    color: ${({ theme }) => theme.colors.white};
  }
`;

const Section = styled.section`
  position: relative;
  padding: ${({ theme }) => theme.spacing.xxxxl} 0;
  @media (min-width: 576px) {
    margin-bottom: ${({ theme }) => theme.spacing.xxxxl};
  }

  &:before {
    content: '';
    background: rgba(40, 40, 40, 0.03);
    @media (min-width: 576px) {
      height: 100%;
    }
    height: 50%;
    @media (min-width: 576px) {
      width: 40%;
    }
    width: 80%;
    top: 0;
    position: absolute;
  }

  .landing-text {
    @media (max-width: 576px) {
      margin-top: ${({ theme }) => theme.spacing.l};
    }
  }
`;

export default Landing;
