import React from 'react';
import { Container, Col, Row } from 'react-grid-system';

import styled from 'styled-components';
import Facebook from '../public/assets/icon/social/facebook.svg';
import Linkedin from '../public/assets/icon/social/linkedin.svg';
import Twitter from '../public/assets/icon/social/twitter.svg';
import Instagram from '../public/assets/icon/social/instagram.svg';
import Icon from './common/Icon';

interface Props {}

const Footer: React.FunctionComponent<Props> = () => {
  return (
    <>
      <Subscribe>
        <Container>
          <Row justify="center">
            <Col lg={8} md={8} sm={12}>
              <h2 className="title">Save Time Save Money</h2>
              <p className="subtitle">
                Signup and we will send you the best deal for vacation
              </p>
              <SubscribeForm>
                <input
                  className="forminput"
                  type="text"
                  placeholder="Enter your email address"
                />
                <button type="submit" className="Subscribebtn">
                  subscribe
                </button>
              </SubscribeForm>
            </Col>
          </Row>
        </Container>
      </Subscribe>

      <MainFooter>
        <div className="footer">
          <Container>
            <Row>
              <Col md={3} sm={6}>
                <FooterTitle>Top Destinations</FooterTitle>
                <NavLinkMenu small>
                  <li>
                    <a href="#">South Africa Travel</a>
                  </li>
                  <li>
                    <a href="#">South Korea Tours</a>
                  </li>
                  <li>
                    <a href="#">Spain Hotels</a>
                  </li>
                  <li>
                    <a href="#">Taiwan Hotels</a>
                  </li>
                  <li>
                    <a href="#">Thailand Travel</a>
                  </li>
                </NavLinkMenu>
              </Col>
              <Col md={3} sm={6}>
                <FooterTitle>Recommended</FooterTitle>
                <NavLinkMenu small>
                  <li>
                    <a href="#">South Africa Travel</a>
                  </li>
                  <li>
                    <a href="#">South Korea Tours</a>
                  </li>
                  <li>
                    <a href="#">Spain Hotels</a>
                  </li>
                  <li>
                    <a href="#">Taiwan Hotels</a>
                  </li>
                </NavLinkMenu>
              </Col>
