import styled from 'styled-components';
import { Container, Col, Row } from 'react-grid-system';

import SwiperCore, { Navigation } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import SliderBlocks from '../../components/common/SliderBlocks';

import { useQuery } from 'react-query';
import { fetchFeaturedHotels } from 'utils/api';
import ItemSearch from '../../components/ItemSearch';

// install Swiper components
SwiperCore.use([Navigation]);

interface Props {}

const Landing: React.FunctionComponent<Props> = () => {
  const { status, data: featuredHotelsData } = useQuery(
    'featuredHotels',
    fetchFeaturedHotels
  );

  return (
    <>
    Hellow world
    </>
  );
};

const SwiperControl = styled.div`

`;

const SlideHolder = styled.div`
  height: 160px;
  @media (min-width: 576px) {
    height: 260px;
  }

  overflow: hidden;
  position: relative;
  img {
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
  .slidetitle {
    position: absolute;
    left: 0;
    bottom: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.55);
    text-align: center;
    padding: ${({ theme }) => theme.spacing.m};
    color: ${({ theme }) => theme.colors.white};
  }
`;

const Section = styled.section`
  position: relative;
  padding: ${({ theme }) => theme.spacing.xxxxl} 0;
  @media (min-width: 576px) {
    margin-bottom: ${({ theme }) => theme.spacing.xxxxl};
  }

  &:before {
    content: '';
    background: rgba(40, 40, 40, 0.03);
    @media (min-width: 576px) {
      height: 100%;
    }
    height: 50%;
    @media (min-width: 576px) {
      width: 40%;
    }
    width: 80%;
    top: 0;
    position: absolute;
  }

  .landing-text {
    @media (max-width: 576px) {
      margin-top: ${({ theme }) => theme.spacing.l};
    }
  }
`;

export default Landing;
